import { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { PlantsAPI } from "../api";
import PlantCard from "../components/PlantCard";
import "./PlantsList.css";

export default function PlantsList() {
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  // Frontend only pagination
  const [currentPage, setCurrentPage] = useState(1);
  const PLANTS_PER_PAGE = 6;

  const location = useLocation();

  const [searchTerm, setSearchTerm] = useState("");
  const [searchField, setSearchField] = useState("none"); // "none" = any field
  const [sortOption, setSortOption] = useState("none");

  // Load plants from backend
  async function load() {
    try {
      const data = await PlantsAPI.list();
      setPlants(data);
      setCurrentPage(1);
    } catch (e) {
      setErr(e.message || "Failed to load");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    load();
  }, []);

  // Read initial values from ?search=, ?field=, ?sort= once
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const initialSearch = params.get("search") || "";
    const initialField = params.get("field") || "none";
    const initialSort = params.get("sort") || "none";

    setSearchTerm(initialSearch);
    setSearchField(initialField);
    setSortOption(initialSort);
    setCurrentPage(1);
  }, [location.search]);

  function handleSearchChange(event) {
    setSearchTerm(event.target.value);
    setCurrentPage(1);
  }

  function handleFieldChange(event) {
    setSearchField(event.target.value);
    setCurrentPage(1);
  }

  function handleSortChange(event) {
    setSortOption(event.target.value);
    setCurrentPage(1);
  }

  async function handleDelete(id) {
    if (!window.confirm("Delete this plant?")) return;

    try {
      await PlantsAPI.delete(id);
      setPlants((list) => {
        const updated = list.filter((p) => p._id !== id);
        const maxPage = Math.max(
          1,
          Math.ceil(updated.length / PLANTS_PER_PAGE)
        );
        if (currentPage > maxPage) {
          setCurrentPage(maxPage);
        }
        return updated;
      });
    } catch (e) {
      alert(e.message || "Delete failed");
    }
  }

  // Helper to safely read a field in lowercase string form
  function lower(value) {
    if (value == null) return "";
    return String(value).toLowerCase();
  }

  // Filtering
  const query = searchTerm.trim().toLowerCase();

  const filteredPlants =
    query === ""
      ? plants
      : plants.filter((plant) => {
          // Try to cover common naming variations
          const common = lower(
            Array.isArray(plant.common_name)
              ? plant.common_name.join(" ")
              : plant.common_name ||
                  plant.CommonName ||
                  plant["common name"]
          );
          const scientific = lower(
            plant.scientific_name ||
              plant.scientificName ||
              plant["scientific name"]
          );
          const description = lower(
            plant.description || plant.notes || plant.Details
          );
          const family = lower(plant.family || plant.Family);
          const genus = lower(
            plant.genus_name ||
              plant.genus ||
              plant.Genus ||
              plant["genus name"]
          );
          const maintenance = lower(
            plant.maintenance_level ||
              plant.maintenance ||
              plant.Maintenance ||
              plant.maintenanceLevel
          );

          if (searchField === "family") {
            return family.includes(query);
          }

          if (searchField === "genus") {
            return genus.includes(query);
          }

          if (searchField === "maintenance") {
            return maintenance.includes(query);
          }

          // "none" = any field
          return (
            common.includes(query) ||
            scientific.includes(query) ||
            description.includes(query) ||
            family.includes(query) ||
            genus.includes(query) ||
            maintenance.includes(query)
          );
        });

  // Sorting A Z by name
  function getNameForSort(plant) {
    const commonArray = Array.isArray(plant.common_name)
      ? plant.common_name
      : [plant.common_name || plant.CommonName || ""];
    const common = commonArray.join(" ").trim();
    const scientific = String(
      plant.scientific_name ||
        plant.scientificName ||
        plant["scientific name"] ||
        ""
    ).trim();
    const description = String(plant.description || plant.notes || "").trim();

    return (common || scientific || description || "").toLowerCase();
  }

  const sortedPlants =
    sortOption === "az"
      ? [...filteredPlants].sort((a, b) =>
          getNameForSort(a).localeCompare(getNameForSort(b))
        )
      : filteredPlants;

  // Pagination
  const totalPages = Math.max(
    1,
    Math.ceil(sortedPlants.length / PLANTS_PER_PAGE)
  );
  const startIndex = (currentPage - 1) * PLANTS_PER_PAGE;
  const currentPlants = sortedPlants.slice(
    startIndex,
    startIndex + PLANTS_PER_PAGE
  );

  function goToPage(page) {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
  }

  const pageNumbers = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <main className="list">
      {/* floating accents */}
      <span className="bubble bubble--green" />
      <span className="bubble bubble--blue" />
      <span className="bubble bubble--purple" />

      <header className="list__header">
        <h2 className="list__title">Your Garden</h2>
        <Link className="btn btn--primary" to="/plants/new">
          + Add Plant
        </Link>
      </header>

      {/* Search bar with dropdowns and button */}
      <section className="list__search">
        <label className="list__search-label" htmlFor="plant-search">
          Search plants
        </label>
        <div className="list__search-row">
          <input
            id="plant-search"
            type="text"
            className="list__search-input"
            placeholder="Type here then use field or sort if needed..."
            value={searchTerm}
            onChange={handleSearchChange}
          />

          <select
            className="list__search-select"
            value={searchField}
            onChange={handleFieldChange}
          >
            <option value="none">Any field</option>
            <option value="family">Family</option>
            <option value="genus">Genus name</option>
            <option value="maintenance">Maintenance level</option>
          </select>

          <select
            className="list__search-select"
            value={sortOption}
            onChange={handleSortChange}
          >
            <option value="none">Sort</option>
            <option value="az">A Z</option>
          </select>

          {/* Button is mostly visual, but also resets to first page */}
          <button
            type="button"
            className="list__search-btn"
            onClick={() => {
              setSearchTerm((prev) => prev.trim());
              setCurrentPage(1);
            }}
          >
            Search
          </button>
        </div>
      </section>

      <section className="list__content">
        {loading && <p className="loading">Loading...</p>}
        {err && <p className="error">{err}</p>}

        {!loading && !err && plants.length === 0 && (
          <div className="empty">
            <p>
              No plants yet. Click <b>Add Plant</b> to create one.
            </p>
          </div>
        )}

        {!loading &&
          !err &&
          plants.length > 0 &&
          filteredPlants.length === 0 && (
            <div className="empty">
              <p>No plants match your search.</p>
            </div>
          )}

        {!loading && !err && filteredPlants.length > 0 && (
          <>
            <div className="grid">
              {currentPlants.map((p) => (
                <PlantCard key={p._id} plant={p} onDelete={handleDelete} />
              ))}
            </div>

            {totalPages > 1 && (
              <div className="list__pagination">
                <button
                  className="list__page-btn"
                  onClick={() => goToPage(currentPage - 1)}
                  disabled={currentPage === 1}
                >
                  Previous
                </button>

                <div className="list__page-numbers">
                  {pageNumbers.map((page) => (
                    <button
                      key={page}
                      className={
                        "list__page-number" +
                        (page === currentPage
                          ? " list__page-number--active"
                          : "")
                      }
                      onClick={() => goToPage(page)}
                    >
                      {page}
                    </button>
                  ))}
                </div>

                <span className="list__page-info">
                  Page {currentPage} of {totalPages}
                </span>

                <button
                  className="list__page-btn"
                  onClick={() => goToPage(currentPage + 1)}
                  disabled={currentPage === totalPages}
                >
                  Next
                </button>
              </div>
            )}
          </>
        )}
      </section>
    </main>
  );
}
