import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { PlantsAPI } from "../api";
import PlantCard from "../components/PlantCard";
import "./PlantsList.css";

export default function PlantsList() {
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  // Frontend only pagination
  const [currentPage, setCurrentPage] = useState(1);
  const PLANTS_PER_PAGE = 6;

  const [searchTerm, setSearchTerm] = useState("");
  const [searchField, setSearchField] = useState("none"); // "none" = Any field

  // Load plants from backend
  async function load() {
    try {
      const data = await PlantsAPI.list();
      setPlants(data);
      setCurrentPage(1);
    } catch (e) {
      setErr(e.message || "Failed to load");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    load();
  }, []);

  // On mount, read ?search= and ?field= once (from Home search)
  useEffect(() => {
    try {
      const params = new URLSearchParams(window.location.search);
      const initialSearch = params.get("search") || "";
      const initialField = params.get("field") || "none";

      setSearchTerm(initialSearch);

      if (
        initialField === "family" ||
        initialField === "genus" ||
        initialField === "maintenance"
      ) {
        setSearchField(initialField);
      } else {
        setSearchField("none");
      }

      setCurrentPage(1);
    } catch {
      // if window or URLSearchParams is not available, just ignore
    }
  }, []);

  function handleSearchChange(event) {
    setSearchTerm(event.target.value);
    setCurrentPage(1);
  }

  function handleFieldChange(event) {
    setSearchField(event.target.value);
    setCurrentPage(1);
  }

  async function handleDelete(id) {
    if (!window.confirm("Delete this plant?")) return;

    try {
      await PlantsAPI.delete(id);
      setPlants((list) => {
        const updated = list.filter((p) => p._id !== id);

        const maxPage = Math.max(
          1,
          Math.ceil(updated.length / PLANTS_PER_PAGE)
        );
        if (currentPage > maxPage) {
          setCurrentPage(maxPage);
        }

        return updated;
      });
    } catch (e) {
      alert(e.message || "Delete failed");
    }
  }

  // Helpers
  function lower(value) {
    if (value == null) return "";
    return String(value).toLowerCase();
  }

  // Find a field by key substring, e.g. "genus" matches genus, genusName, genus_name
  function getFieldByKeySubstring(plant, substring) {
    const sub = substring.toLowerCase();

    for (const key in plant) {
      if (!Object.prototype.hasOwnProperty.call(plant, key)) continue;
      if (key.toLowerCase().includes(sub)) {
        const value = plant[key];
        if (value == null) return "";

        if (
          typeof value === "string" ||
          typeof value === "number" ||
          typeof value === "boolean"
        ) {
          return String(value);
        }

        if (Array.isArray(value)) {
          return value.join(" ");
        }

        if (typeof value === "object") {
          // nested object: flatten to string
          try {
            return JSON.stringify(value);
          } catch {
            return "";
          }
        }
      }
    }

    return "";
  }

  // ---------- FILTERING (according to your rules) ----------

  const query = searchTerm.trim().toLowerCase();

  const filteredPlants =
    query === ""
      ? plants
      : plants.filter((plant) => {
          if (searchField === "family") {
            const familyValue = lower(getFieldByKeySubstring(plant, "family"));
            return familyValue.includes(query);
          }

          if (searchField === "genus") {
            const genusValue = lower(getFieldByKeySubstring(plant, "genus"));
            return genusValue.includes(query);
          }

          if (searchField === "maintenance") {
            const maintValue = lower(
              getFieldByKeySubstring(plant, "maintenance")
            );
            return maintValue.includes(query);
          }

          // "none" (Any field) → search entire plant object
          let full = "";
          try {
            full = lower(JSON.stringify(plant));
          } catch {
            full = "";
          }
          return full.includes(query);
        });

  // ---------- PAGINATION (unchanged) ----------

  const totalPages = Math.max(
    1,
    Math.ceil(filteredPlants.length / PLANTS_PER_PAGE)
  );
  const startIndex = (currentPage - 1) * PLANTS_PER_PAGE;
  const currentPlants = filteredPlants.slice(
    startIndex,
    startIndex + PLANTS_PER_PAGE
  );

  function goToPage(page) {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
  }

  const pageNumbers = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <main className="list">
      {/* floating accents */}
      <span className="bubble bubble--green" />
      <span className="bubble bubble--blue" />
      <span className="bubble bubble--purple" />

      <header className="list__header">
        <h2 className="list__title">Your Garden</h2>
        <Link className="btn btn--primary" to="/plants/new">
          + Add Plant
        </Link>
      </header>

      {/* Search bar with dropdown and button */}
      <section className="list__search">
        <label className="list__search-label" htmlFor="plant-search">
          Search plants
        </label>
        <div className="list__search-row">
          <input
            id="plant-search"
            type="text"
            className="list__search-input"
            placeholder="Type here (e.g. Monstera)..."
            value={searchTerm}
            onChange={handleSearchChange}
          />

          <select
            className="list__search-select"
            value={searchField}
            onChange={handleFieldChange}
          >
            <option value="none">Any field</option>
            <option value="family">Family</option>
            <option value="genus">Genus name</option>
            <option value="maintenance">Maintenance level</option>
          </select>

          <button
            type="button"
            className="list__search-btn"
            onClick={() => {
              // Visual action: trim and go back to first page
              setSearchTerm((prev) => prev.trim());
              setCurrentPage(1);
            }}
          >
            Search
          </button>
        </div>
      </section>

      <section className="list__content">
        {loading && <p className="loading">Loading...</p>}
        {err && <p className="error">{err}</p>}

        {!loading && !err && plants.length === 0 && (
          <div className="empty">
            <p>
              No plants yet. Click <b>Add Plant</b> to create one.
            </p>
          </div>
        )}

        {!loading &&
          !err &&
          plants.length > 0 &&
          filteredPlants.length === 0 && (
            <div className="empty">
              <p>No plants match your search.</p>
            </div>
          )}

        {!loading && !err && filteredPlants.length > 0 && (
          <>
            <div className="grid">
              {currentPlants.map((p) => (
                <PlantCard key={p._id} plant={p} onDelete={handleDelete} />
              ))}
            </div>

            {totalPages > 1 && (
              <div className="list__pagination">
                <button
                  className="list__page-btn"
                  onClick={() => goToPage(currentPage - 1)}
                  disabled={currentPage === 1}
                >
                  Previous
                </button>

                <div className="list__page-numbers">
                  {pageNumbers.map((page) => (
                    <button
                      key={page}
                      className={
                        "list__page-number" +
                        (page === currentPage
                          ? " list__page-number--active"
                          : "")
                      }
                      onClick={() => goToPage(page)}
                    >
                      {page}
                    </button>
                  ))}
                </div>

                <span className="list__page-info">
                  Page {currentPage} of {totalPages}
                </span>

                <button
                  className="list__page-btn"
                  onClick={() => goToPage(currentPage + 1)}
                  disabled={currentPage === totalPages}
                >
                  Next
                </button>
              </div>
            )}
          </>
        )}
      </section>
    </main>
  );
}
