import { useEffect, useState } from "react";
import { Link, useSearchParams } from "react-router-dom";
import { PlantsAPI } from "../api";
import PlantCard from "../components/PlantCard";
import "./PlantsList.css";

export default function PlantsList() {
  const [plants, setPlants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  // Frontend only pagination
  const [currentPage, setCurrentPage] = useState(1);
  const PLANTS_PER_PAGE = 6; // change if you want more or fewer per page

  // Read initial values from URL (if coming from Home search)
  const [searchParams] = useSearchParams();
  const [searchTerm, setSearchTerm] = useState(
    () => searchParams.get("search") || ""
  );
  const [searchField, setSearchField] = useState(
    () => searchParams.get("field") || "family"
  );
  const [sortOption, setSortOption] = useState(
    () => searchParams.get("sort") || "none"
  );

  async function load() {
    try {
      const data = await PlantsAPI.list();
      setPlants(data);
      setCurrentPage(1); // reset to first page when data reloads
    } catch (e) {
      setErr(e.message || "Failed to load");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    load();
  }, []);

  function handleSearchChange(event) {
    const value = event.target.value;
    setSearchTerm(value);
    setCurrentPage(1);
  }

  function handleFieldChange(event) {
    const value = event.target.value;
    setSearchField(value);
    setCurrentPage(1);
  }

  function handleSortChange(event) {
    const value = event.target.value;
    setSortOption(value);
    setCurrentPage(1);
  }

  async function handleDelete(id) {
    if (!window.confirm("Delete this plant?")) return;

    try {
      await PlantsAPI.delete(id);
      setPlants((list) => {
        const updated = list.filter((p) => p._id !== id);

        // adjust page if we deleted the last item on the last page
        const maxPage = Math.max(
          1,
          Math.ceil(updated.length / PLANTS_PER_PAGE)
        );
        if (currentPage > maxPage) {
          setCurrentPage(maxPage);
        }

        return updated;
      });
    } catch (e) {
      alert(e.message || "Delete failed");
    }
  }

  // Filter plants based on search term and selected field only
  const query = searchTerm.trim().toLowerCase();

  const filteredPlants =
    query === ""
      ? plants
      : plants.filter((plant) => {
          let target = "";

          if (searchField === "family") {
            target = String(plant.family || "").toLowerCase();
          } else if (searchField === "genus") {
            target = String(
              plant.genus_name ||
                plant.genus ||
                plant.genusName ||
                ""
            ).toLowerCase();
          } else if (searchField === "maintenance") {
            target = String(
              plant.maintenance_level ||
                plant.maintenance ||
                plant.maintenanceLevel ||
                ""
            ).toLowerCase();
          }

          // If the field is missing, target will be empty and not match anything
          return target.includes(query);
        });

  // Helper for A-Z sort (still using names for nice ordering)
  function getNameForSort(plant) {
    const commonArray = Array.isArray(plant.common_name)
      ? plant.common_name
      : [plant.common_name || ""];
    const common = commonArray.join(" ").trim();
    const scientific = String(plant.scientific_name || "").trim();
    const description = String(plant.description || "").trim();

    return (common || scientific || description || "").toLowerCase();
  }

  const sortedPlants =
    sortOption === "az"
      ? [...filteredPlants].sort((a, b) =>
          getNameForSort(a).localeCompare(getNameForSort(b))
        )
      : filteredPlants;

  // pagination math (based on sortedPlants)
  const totalPages = Math.max(
    1,
    Math.ceil(sortedPlants.length / PLANTS_PER_PAGE)
  );
  const startIndex = (currentPage - 1) * PLANTS_PER_PAGE;
  const currentPlants = sortedPlants.slice(
    startIndex,
    startIndex + PLANTS_PER_PAGE
  );

  function goToPage(page) {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
  }

  // build array [1, 2, ..., totalPages] for clickable page numbers
  const pageNumbers = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <main className="list">
      {/* floating accents */}
      <span className="bubble bubble--green" />
      <span className="bubble bubble--blue" />
      <span className="bubble bubble--purple" />

      <header className="list__header">
        <h2 className="list__title">Your Garden</h2>
        <Link className="btn btn--primary" to="/plants/new">
          + Add Plant
        </Link>
      </header>

      {/* Search bar with dropdowns and button */}
      <section className="list__search">
        <label className="list__search-label" htmlFor="plant-search">
          Search plants
        </label>
        <div className="list__search-row">
          <input
            id="plant-search"
            type="text"
            className="list__search-input"
            placeholder="Search by selected field..."
            value={searchTerm}
            onChange={handleSearchChange}
          />

          <select
            className="list__search-select"
            value={searchField}
            onChange={handleFieldChange}
          >
            <option value="family">Family</option>
            <option value="genus">Genus name</option>
            <option value="maintenance">Maintenance level</option>
          </select>

          <select
            className="list__search-select"
            value={sortOption}
            onChange={handleSortChange}
          >
            <option value="none">Sort</option>
            <option value="az">A-Z</option>
          </select>

          <button
            type="button"
            className="list__search-btn"
            onClick={() => {
              // Explicit click just re-trims and resets page
              setSearchTerm((prev) => prev.trim());
              setCurrentPage(1);
            }}
          >
            Search
          </button>
        </div>
      </section>

      <section className="list__content">
        {loading && <p className="loading">Loading...</p>}
        {err && <p className="error">{err}</p>}

        {!loading && !err && plants.length === 0 && (
          <div className="empty">
            <p>
              No plants yet. Click <b>Add Plant</b> to create one.
            </p>
          </div>
        )}

        {!loading &&
          !err &&
          plants.length > 0 &&
          filteredPlants.length === 0 && (
            <div className="empty">
              <p>No plants match your search.</p>
            </div>
          )}

        {!loading && !err && filteredPlants.length > 0 && (
          <>
            <div className="grid">
              {currentPlants.map((p) => (
                <PlantCard key={p._id} plant={p} onDelete={handleDelete} />
              ))}
            </div>

            {totalPages > 1 && (
              <div className="list__pagination">
                <button
                  className="list__page-btn"
                  onClick={() => goToPage(currentPage - 1)}
                  disabled={currentPage === 1}
                >
                  Previous
                </button>

                <div className="list__page-numbers">
                  {pageNumbers.map((page) => (
                    <button
                      key={page}
                      className={
                        "list__page-number" +
                        (page === currentPage
                          ? " list__page-number--active"
                          : "")
                      }
                      onClick={() => goToPage(page)}
                    >
                      {page}
                    </button>
                  ))}
                </div>

                <span className="list__page-info">
                  Page {currentPage} of {totalPages}
                </span>

                <button
                  className="list__page-btn"
                  onClick={() => goToPage(currentPage + 1)}
                  disabled={currentPage === totalPages}
                >
                  Next
                </button>
              </div>
            )}
          </>
        )}
      </section>
    </main>
  );
}
